$(document).ready(function() {
    $("#trigger").click(function() {
        $(this).toggleClass("rotate-90");
        $("#main-menu").toggleClass("show-menu")
    })
});
if ($(window).width() > 960) {
    $(function() {
        $(window).scroll(function() {
            if ($(this).scrollTop() >= 50) {
                $("div#header").addClass("stickytop");
                $("ul.main-menu li img").addClass("navbar-list-img");
                $('nav.main-nav>ul.main-menu>li.has-sub-menu>ul.sub-menu').addClass('ul-2-padding-top');
            } else {
                $("div#header").removeClass("stickytop");
                $("ul.main-menu li img").removeClass("navbar-list-img");
                $('nav.main-nav>ul.main-menu>li.has-sub-menu>ul.sub-menu').removeClass('ul-2-padding-top');
            }
        })
    })
} else {};